      //   //   myCanvasContext.drawImage(img, 0, 0);
      //   // };
      //   // img.src = e.data;
      //   // imageOupput = "data:image/png;base64, " + e.data;
      //   // console.log(img);

      //   // var node = document.createElement("img");
